.text-modern {
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 2px;
}

* {
  transition: all 0.3s ease;
  box-sizing: border-box;
}

html, body {
  background-color: #3e4041;
}

body {
  width: 100vw;
  height: 100vh;
  line-height: initial;
  font-family: sofia-pro, sans-serif;
}
body.sesam-show #app {
  filter: blur(10px);
}

img {
  user-select: none;
  -webkit-user-drag: none;
  pointer-events: none;
}

a, a:hover, a:active {
  color: inherit;
  text-decoration: none;
}

#app {
  color: white;
}

input {
  outline: none !important;
  box-shadow: none !important;
}

.btn {
  display: block;
  width: fit-content;
  white-space: nowrap;
  background-color: #5050bf;
  text-transform: uppercase;
  color: white;
  letter-spacing: 2px;
  font-weight: bold;
  font-size: 80%;
}
.btn:active, .btn:hover, .btn:focus, .btn:focus-within {
  outline: none;
  box-shadow: none;
  color: white;
}
.btn:active {
  opacity: 0.7;
}
.btn--simple {
  position: relative;
  background-color: transparent;
  padding-left: 0px;
  padding-right: 0px;
}

.modal {
  position: fixed;
  left: 0px;
  right: 0px;
  top: 0px;
  bottom: 0px;
  display: none;
  place-items: center;
  padding: 1.8rem;
  z-index: 10;
}
.modal.sesam-show {
  display: grid;
}
.modal__header {
  margin-bottom: 2rem;
}
.modal__content {
  background-color: white;
  border-radius: 14px;
  padding: 1.6rem;
  min-width: 50vw;
  max-width: 750px;
}
.modal__content h4 {
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 2px;
  font-size: 1rem;
  font-weight: 600;
  color: grey;
  margin-bottom: 0.6rem;
}
.modal__footer {
  display: flex;
  margin-top: 1.6rem;
  justify-content: center;
}
.modal__footer .btn {
  margin-right: 1rem;
}
.modal__footer .btn:last-child {
  margin-right: 0;
}

.clock {
  width: fit-content;
  margin-top: -5rem;
  font-size: 10rem;
  margin-bottom: -2rem;
}

.details {
  display: flex;
  width: fit-content;
  margin-right: auto;
}
.details__devider {
  margin: 0 5px;
}
.details__weather .bx {
  margin-right: 0.2rem;
  font-size: 24px;
  transform: translateY(2px);
}
.details__weather .bx.bxs-sun {
  color: #ffd607;
}
.details__weather .bx.bx-cloud-rain, .details__weather .bx.bx-cloud-lightning {
  color: #7bc2fd !important;
}

[data-label=searchResults] {
  position: fixed;
  bottom: 0px;
  left: 0px;
  right: 0px;
  padding: 1rem;
  background-color: white;
  color: grey;
  border-radius: 22px 22px 0px 0px;
  z-index: 10;
}
[data-label=searchResults]:not(.animate__fadeInUp) {
  bottom: -100%;
}
[data-label=searchResults] .result {
  display: flex;
  flex-direction: row;
  height: 85px;
  background-color: #efefef;
  border-radius: 14px;
  color: inherit;
  text-decoration: none;
}
[data-label=searchResults] .result > div:nth-child(2) {
  padding: 1.2rem;
}
[data-label=searchResults] .result__thumb img {
  width: 85px;
  height: 85px;
  object-fit: cover;
  object-position: center;
  border-radius: 14px 0px 0px 14px;
}
[data-label=searchResults] .result__domain {
  display: flex;
  align-items: center;
  margin-bottom: 0.4rem;
}
[data-label=searchResults] .result__favicon {
  display: flex;
  align-items: center;
  margin-right: 0.4rem;
}
[data-label=searchResults] .result__favicon img {
  height: 15px;
}
[data-label=searchResults] .result__domain-name {
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 80%;
  font-weight: 700;
}
[data-label=searchResults] .result__title {
  text-overflow: ellipsis;
  overflow: hidden;
}
[data-label=searchResults] .result__snippet {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 34ch;
}

[data-form=searchEngine] .input-wrapper {
  display: flex;
  align-items: center;
  width: fit-content;
  padding: 0 0 0 0.8rem;
  border-radius: 120px;
  border: 1px solid grey;
}
[data-form=searchEngine] .input-wrapper input {
  font-family: system-ui, -apple-system, "Segoe UI", Roboto;
  display: block;
  height: 40px;
  width: 50vw;
  max-width: 50vw;
  appearance: none;
  border: none;
  background: none;
  color: white;
}
[data-form=searchEngine] .input-wrapper input::placeholder {
  font-family: "sofia-pro";
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: 500;
  font-size: 80%;
  color: #b6b6b6;
  vertical-align: middle;
}
[data-form=searchEngine] .input-wrapper .bx {
  font-size: 1.4rem;
  transform: translateY(1px);
}
[data-form=searchEngine] .input-wrapper button {
  height: 100%;
  background: none;
}

@media (min-width: 768px) {
  [data-label=searchResults] {
    left: 30px;
    right: 30px;
    padding: 2rem;
  }
}
[data-section=corona] {
  display: none;
}
[data-section=corona].sesam-show {
  display: block;
}

[data-section=corona] .corona__wrapper {
  width: 100%;
  margin: 0 auto;
  padding: 1rem 1rem 4rem 1rem;
  border: 1px solid grey;
  border-radius: 19px 19px 0px 0px;
  border-radius: 19px;
}
[data-section=corona] .corona__stats {
  display: flex;
  width: 100%;
}
[data-section=corona] .corona__stats > *:first-child {
  border-radius: 120px 0px 0px 120px;
}
[data-section=corona] .corona__stats > *:last-child {
  border-radius: 0px 120px 120px 0px;
}
[data-section=corona] .corona__stats > * {
  position: relative;
  height: 20px;
}
[data-section=corona] .corona__stats .info {
  position: absolute;
  top: calc(100% + 10px);
  left: 0px;
  right: 0px;
  display: flex;
  justify-content: center;
  align-items: center;
}
[data-section=corona] .corona__stats .info__wrapper {
  position: relative;
  width: fit-content;
  padding: 0.2rem 0.4rem 0.3rem 0.4rem;
  background-color: #212121;
  border-radius: 120px;
  white-space: nowrap;
  font-size: 75%;
  text-align: center;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 2px;
  font-weight: normal;
}
[data-section=corona] .corona__stats .info__wrapper::after {
  --size: 5px;
  content: "";
  position: absolute;
  left: 50%;
  right: 50%;
  bottom: 100%;
  width: 0;
  height: 0;
  border-left: var(--size) solid transparent;
  border-right: var(--size) solid transparent;
  border-bottom: var(--size) solid #212121;
  clear: both;
}
[data-section=corona] .corona__deaths {
  background-color: #212121;
}
[data-section=corona] .corona__cases {
  background-color: #363636;
}
[data-section=corona] .corona__clean {
  background-color: #504f4f;
}

.sesam-backdrop {
  position: fixed;
  top: 0px;
  bottom: 0px;
  left: 0px;
  right: 0px;
  z-index: 9;
  background-color: rgba(0, 0, 0, 0.336);
}
.sesam-backdrop.sesam-hidden {
  display: none;
}

